mapper.select:
select
a.WEALTH_ID as wealthId, a.CUST_ID as custId, a.ECIF_CUST_ID as ecifCustId, a.CUST_NAME as custName, a.LAW_ORG_ID as lawOrgId, a.OPEN_ORG_ID as openOrgId, a.PROD_CODE as prodCode, a.PROD_NAME as prodName, a.PROD_TYPE as prodType, a.RISK_LEVEL as riskLevel, a.PUBLIC_ORG as publicOrg, a.PERF_COMPARE_BASE as perfCompareBase, a.CURRENT_BAL as currentBal, a.INTEREST_DATE as interestDate, a.END_DATE as endDate, a.HOLD_PROFIT as holdProfit, a.HOLD_PROFIT_RATE as holdProfitRate from T_PCM_WEALTH a
order by ${dto.orderBy}
